import React, { useState, useEffect } from 'react';
import './App.css';
import { FaShoppingCart, FaTshirt, FaFemale, FaMale, FaChild, FaHeadphones, FaCamera, FaGamepad, FaLaptop, FaTabletAlt, FaClock, FaVolumeUp, FaTimes, FaSearchPlus, FaShoePrints, FaKeyboard, FaMobileAlt, FaTv, FaCameraRetro, FaStar, FaTag, FaSuitcase, FaAppleAlt, FaUserCircle, FaSearch } from 'react-icons/fa';

const categories = [
  { name: 'All', icon: <FaSearchPlus /> },
  { name: 'Electronics', icon: <FaClock /> },
  { name: 'Wearables', icon: <FaTshirt /> },
  { name: 'Audio', icon: <FaVolumeUp /> },
  { name: 'Photography', icon: <FaCamera /> },
  { name: 'Gaming', icon: <FaGamepad /> },
  { name: 'Computers', icon: <FaLaptop /> },
  { name: 'Tablets', icon: <FaTabletAlt /> },
  { name: 'Men', icon: <FaMale /> },
  { name: 'Women', icon: <FaFemale /> },
  { name: 'Kids', icon: <FaChild /> },
];

const sampleProducts = [
  { id: 1, name: 'Smart Watch', price: 99, category: 'Wearables', img: 'https://images.unsplash.com/photo-1511707171634-5f897ff02aa9?auto=format&fit=crop&w=400&q=80', icon: <FaClock />, rating: 4.5, badge: 'Best Seller', desc: 'Track your fitness and notifications with this stylish smart watch.' },
  { id: 2, name: 'Wireless Headphones', price: 59, category: 'Audio', img: 'https://images.unsplash.com/photo-1511367461989-f85a21fda167?auto=format&fit=crop&w=400&q=80', icon: <FaHeadphones />, rating: 4.7, badge: 'Best Seller', desc: 'Enjoy high-quality sound and comfort with these wireless headphones.' },
  { id: 3, name: 'Gaming Mouse', price: 39, category: 'Gaming', img: 'https://images.unsplash.com/photo-1517336714731-489689fd1ca8?auto=format&fit=crop&w=400&q=80', icon: <FaGamepad />, rating: 4.2, badge: 'New', desc: 'Precision and speed for your gaming sessions.' },
  { id: 4, name: 'DSLR Camera', price: 499, category: 'Photography', img: 'https://images.unsplash.com/photo-1519125323398-675f0ddb6308?auto=format&fit=crop&w=400&q=80', icon: <FaCameraRetro />, rating: 4.8, badge: 'Best Seller', desc: 'Capture stunning photos with this professional DSLR camera.' },
  { id: 5, name: 'Laptop', price: 899, category: 'Computers', img: 'https://images.unsplash.com/photo-1517336714731-489689fd1ca8?auto=format&fit=crop&w=400&q=80', icon: <FaLaptop />, rating: 4.6, badge: '', desc: 'Powerful laptop for work and play.' },
  { id: 6, name: 'Tablet', price: 299, category: 'Tablets', img: 'https://images.unsplash.com/photo-1515378791036-0648a3ef77b2?auto=format&fit=crop&w=400&q=80', icon: <FaTabletAlt />, rating: 4.1, badge: 'New', desc: 'Portable and versatile tablet for all your needs.' },
  { id: 7, name: "Men's T-Shirt", price: 19, category: 'Men', img: 'https://images.unsplash.com/photo-1512436991641-6745cdb1723f?auto=format&fit=crop&w=400&q=80', icon: <FaTshirt />, rating: 4.0, badge: '', desc: 'Comfortable and stylish t-shirt for men.' },
  { id: 8, name: "Women's Dress", price: 29, category: 'Women', img: 'https://images.unsplash.com/photo-1517841905240-472988babdf9?auto=format&fit=crop&w=400&q=80', icon: <FaFemale />, rating: 4.3, badge: '', desc: 'Elegant dress for women.' },
  { id: 9, name: "Kids' Jacket", price: 25, category: 'Kids', img: 'https://images.unsplash.com/photo-1503342217505-b0a15ec3261c?auto=format&fit=crop&w=400&q=80', icon: <FaChild />, rating: 4.2, badge: 'New', desc: 'Warm and cozy jacket for kids.' },
  { id: 10, name: 'Bluetooth Speaker', price: 45, category: 'Audio', img: 'https://images.unsplash.com/photo-1465101046530-73398c7f28ca?auto=format&fit=crop&w=400&q=80', icon: <FaVolumeUp />, rating: 4.4, badge: '', desc: 'Portable speaker with deep bass.' },
  { id: 11, name: 'Fitness Tracker', price: 79, category: 'Wearables', img: 'https://images.unsplash.com/photo-1519864600265-abb23847ef2c?auto=format&fit=crop&w=400&q=80', icon: <FaClock />, rating: 4.0, badge: '', desc: 'Track your health and activity.' },
  { id: 12, name: 'Camera Lens', price: 199, category: 'Photography', img: 'https://images.unsplash.com/photo-1464983953574-0892a716854b?auto=format&fit=crop&w=400&q=80', icon: <FaCamera />, rating: 4.5, badge: '', desc: 'High-quality lens for your camera.' },
  { id: 13, name: 'Gaming Keyboard', price: 69, category: 'Gaming', img: 'https://images.unsplash.com/photo-1517336714731-489689fd1ca8?auto=format&fit=crop&w=400&q=80', icon: <FaKeyboard />, rating: 4.3, badge: '', desc: 'RGB backlit keyboard for gamers.' },
  { id: 14, name: 'Backpack', price: 49, category: 'Men', img: 'https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=400&q=80', icon: <FaSuitcase />, rating: 4.1, badge: '', desc: 'Durable and spacious backpack.' },
  { id: 15, name: 'Running Shoes', price: 89, category: 'Women', img: 'https://images.unsplash.com/photo-1513104890138-7c749659a591?auto=format&fit=crop&w=400&q=80', icon: <FaShoePrints />, rating: 4.6, badge: 'Best Seller', desc: 'Lightweight running shoes for women.' },
  { id: 16, name: 'Smartphone', price: 499, category: 'Electronics', img: 'https://images.unsplash.com/photo-1510557880182-3d4d3c1b2604?auto=format&fit=crop&w=400&q=80', icon: <FaMobileAlt />, rating: 4.7, badge: '', desc: 'Latest smartphone with advanced features.' },
  { id: 17, name: '4K TV', price: 799, category: 'Electronics', img: 'https://images.unsplash.com/photo-1519125323398-675f0ddb6308?auto=format&fit=crop&w=400&q=80', icon: <FaTv />, rating: 4.8, badge: 'Best Seller', desc: 'Ultra HD 4K television for your home.' },
  { id: 18, name: 'Apple', price: 2, category: 'Kids', img: 'https://images.unsplash.com/photo-1502741338009-cac2772e18bc?auto=format&fit=crop&w=400&q=80', icon: <FaAppleAlt />, rating: 4.0, badge: '', desc: 'Fresh and juicy apple.' },
  { id: 19, name: 'Sale Tag', price: 5, category: 'Kids', img: 'https://images.unsplash.com/photo-1503342217505-b0a15ec3261c?auto=format&fit=crop&w=400&q=80', icon: <FaTag />, rating: 4.1, badge: 'New', desc: 'Special sale tag for kids products.' },
  { id: 20, name: 'Star Toy', price: 15, category: 'Kids', img: 'https://images.unsplash.com/photo-1503342217505-b0a15ec3261c?auto=format&fit=crop&w=400&q=80', icon: <FaStar />, rating: 4.3, badge: '', desc: 'Fun star-shaped toy for kids.' },
];

function getStars(rating) {
  const full = Math.floor(rating);
  const half = rating % 1 >= 0.5;
  return [
    ...Array(full).fill(<FaStar style={{ color: '#FF9900', marginRight: 2 }} />),
    ...(half ? [<FaStar style={{ color: '#FFCC80', marginRight: 2 }} />] : []),
  ];
}

function App() {
  const [cart, setCart] = useState(() => JSON.parse(localStorage.getItem('cart')) || []);
  const [category, setCategory] = useState('All');
  const [cartOpen, setCartOpen] = useState(false);
  const [toast, setToast] = useState('');
  const [quickView, setQuickView] = useState(null);
  const [search, setSearch] = useState('');

  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(cart));
  }, [cart]);

  useEffect(() => {
    if (toast) {
      const t = setTimeout(() => setToast(''), 2000);
      return () => clearTimeout(t);
    }
  }, [toast]);

  const filteredProducts = sampleProducts.filter(p =>
    (category === 'All' || p.category === category) &&
    (p.name.toLowerCase().includes(search.toLowerCase()) || p.desc.toLowerCase().includes(search.toLowerCase()))
  );

  const addToCart = (product) => {
    setCart(prev => {
      const exists = prev.find(item => item.id === product.id);
      if (exists) {
        setToast('Increased quantity in cart!');
        return prev.map(item => item.id === product.id ? { ...item, qty: item.qty + 1 } : item);
      }
      setToast('Added to cart!');
      return [...prev, { ...product, qty: 1 }];
    });
  };

  const removeFromCart = (id) => {
    setCart(prev => prev.filter(item => item.id !== id));
    setToast('Removed from cart!');
  };

  const removeAllCart = () => {
    setCart([]);
    setToast('Cart cleared!');
  };

  const updateQty = (id, qty) => {
    setCart(prev => prev.map(item => item.id === id ? { ...item, qty } : item));
  };

  const total = cart.reduce((a, c) => a + c.price * c.qty, 0);

  return (
    <>
      {/* Navbar */}
      <nav className="navbar">
        <span className="navbar-brand" style={{ fontWeight: 800, fontSize: '2rem', letterSpacing: 2 }}>E-Shop</span>
        <div style={{ display: 'flex', alignItems: 'center', gap: 16, flex: 1, maxWidth: 600, margin: '0 2rem' }}>
          <div className="search-bar" style={{ flex: 1, position: 'relative' }}>
            <input
              type="text"
              className="form-control"
              placeholder="Search products..."
              value={search}
              onChange={e => setSearch(e.target.value)}
              style={{ borderRadius: 24, paddingLeft: 40, fontSize: 16 }}
            />
            <FaSearch style={{ position: 'absolute', left: 12, top: 12, color: '#FF9900', fontSize: 18 }} />
          </div>
        </div>
        <button className="btn btn-category" onClick={() => setCartOpen(true)}>
          <FaShoppingCart /> Cart ({cart.reduce((a, c) => a + c.qty, 0)})
        </button>
        <FaUserCircle style={{ fontSize: 32, color: '#FF9900', marginLeft: 24, cursor: 'pointer' }} title="Account" />
      </nav>

      {/* Hero */}
      <div className="hero mt-4">
        <h1 style={{ fontWeight: 900, fontSize: '2.5rem', marginBottom: 8 }}>Modern E-Commerce Store</h1>
        <p style={{ fontSize: 18, color: '#555', marginBottom: 0 }}>Beautiful, interactive shopping experience. Browse by category and add to cart!</p>
      </div>

      {/* Category Filter */}
      <div className="category-filter mb-4">
        {categories.map(cat => (
          <button
            key={cat.name}
            className={`btn btn-category${category === cat.name ? ' active' : ''}`}
            onClick={() => setCategory(cat.name)}
          >
            {cat.icon} {cat.name}
          </button>
        ))}
      </div>

      {/* Product Grid */}
      <div className="product-grid">
        {filteredProducts.map(product => (
          <div className="product-card" key={product.id}>
            {product.badge && <span className="badge" style={{ position: 'absolute', top: 16, left: 16, background: '#FF9900', color: '#fff', borderRadius: 12, padding: '2px 12px', fontWeight: 600, fontSize: 13 }}>{product.badge}</span>}
            <img src={product.img} alt={product.name} />
            <h5 style={{ display: 'flex', alignItems: 'center', gap: 8, fontWeight: 700, fontSize: 18, margin: '8px 0 4px 0', whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis', width: '100%' }}>{product.icon} {product.name}</h5>
            <div style={{ display: 'flex', alignItems: 'center', gap: 4, marginBottom: 4 }}>{getStars(product.rating)}<span style={{ color: '#888', fontSize: 13, marginLeft: 4 }}>{product.rating}</span></div>
            <p className="text-muted" style={{ fontSize: 14, minHeight: 36, margin: '0 0 8px 0', color: '#666' }}>{product.desc}</p>
            <p className="fw-bold" style={{ fontSize: 18, margin: 0 }}>${product.price}</p>
            <button className="btn btn-primary w-100 mb-2" style={{ borderRadius: 24, fontWeight: 600, background: '#FF9900', border: 'none', marginTop: 8 }} onClick={() => addToCart(product)}>
              Add to Cart
            </button>
            <button className="btn btn-outline-secondary w-100 mb-2" style={{ borderRadius: 24, fontWeight: 600, marginBottom: 8 }} onClick={() => setQuickView(product)}>
              View Details
            </button>
            <button className="quick-view" title="Quick View" onClick={() => setQuickView(product)}>
              <FaSearchPlus />
            </button>
          </div>
        ))}
      </div>

      {/* Cart Drawer */}
      <div className={`cart-drawer${cartOpen ? ' open' : ''}`}>
        <div className="cart-drawer-header">
          <h5 style={{ fontWeight: 700, fontSize: 20 }}><FaShoppingCart /> Cart</h5>
          <button className="cart-drawer-close" onClick={() => setCartOpen(false)}><FaTimes /></button>
        </div>
        <div className="cart-drawer-list">
          {cart.length === 0 ? <p className="text-muted">Cart is empty</p> : (
            <ul className="list-unstyled">
              {cart.map(item => (
                <li key={item.id} className="d-flex align-items-center mb-2">
                  <span style={{ fontSize: 20, marginRight: 6 }}>{item.icon}</span>
                  <img src={item.img} alt={item.name} style={{ width: 36, height: 36, marginRight: 8, borderRadius: 8 }} />
                  <span className="me-2" style={{ fontWeight: 600 }}>{item.name}</span>
                  <input type="number" min="1" value={item.qty} onChange={e => updateQty(item.id, Number(e.target.value))} style={{ width: 50 }} className="form-control form-control-sm mx-2" />
                  <span className="fw-bold">${item.price * item.qty}</span>
                  <button className="btn btn-danger btn-sm ms-2" style={{ borderRadius: 12 }} onClick={() => removeFromCart(item.id)}>&times;</button>
                </li>
              ))}
            </ul>
          )}
        </div>
        <div className="cart-drawer-footer">
          <div className="fw-bold" style={{ fontSize: 18 }}>Total: ${total}</div>
          <button className="btn btn-success mt-2 w-100" style={{ borderRadius: 24, fontWeight: 600, background: '#FF9900', border: 'none' }} disabled={cart.length === 0}>Checkout</button>
          <button className="btn btn-outline-secondary mt-2 w-100" style={{ borderRadius: 24, fontWeight: 600 }} onClick={removeAllCart} disabled={cart.length === 0}>Remove All</button>
          <button className="btn btn-link mt-2 w-100" style={{ borderRadius: 24, fontWeight: 600, color: '#FF9900', textDecoration: 'underline' }} onClick={() => setCartOpen(false)}>Continue Shopping</button>
        </div>
      </div>

      {/* Toast Notification */}
      <div className={`toast${toast ? ' show' : ''}`}>{toast}</div>

      {/* Quick View Modal */}
      {quickView && (
        <div style={{
          position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh',
          background: 'rgba(0,0,0,0.3)', zIndex: 3000, display: 'flex', alignItems: 'center', justifyContent: 'center'
        }} onClick={() => setQuickView(null)}>
          <div style={{ background: '#fff', borderRadius: 16, padding: 32, minWidth: 320, minHeight: 220, position: 'relative', boxShadow: '0 8px 32px rgba(0,0,0,0.16)', maxWidth: 400 }} onClick={e => e.stopPropagation()}>
            <button style={{ position: 'absolute', top: 12, right: 12, background: 'none', border: 'none', fontSize: 24, color: '#FF9900', cursor: 'pointer' }} onClick={() => setQuickView(null)}><FaTimes /></button>
            {quickView.badge && <span className="badge" style={{ position: 'absolute', top: 16, left: 16, background: '#FF9900', color: '#fff', borderRadius: 12, padding: '2px 12px', fontWeight: 600, fontSize: 13 }}>{quickView.badge}</span>}
            <img src={quickView.img} alt={quickView.name} style={{ width: 140, height: 140, objectFit: 'contain', marginBottom: 16, borderRadius: 12 }} />
            <h4 style={{ display: 'flex', alignItems: 'center', gap: 8, fontWeight: 700, fontSize: 22, margin: '8px 0 4px 0', whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis', width: '100%' }}>{quickView.icon} {quickView.name}</h4>
            <div style={{ display: 'flex', alignItems: 'center', gap: 4, marginBottom: 4 }}>{getStars(quickView.rating)}<span style={{ color: '#888', fontSize: 13, marginLeft: 4 }}>{quickView.rating}</span></div>
            <p className="text-muted" style={{ fontSize: 15, minHeight: 36, margin: '0 0 8px 0', color: '#666' }}>{quickView.desc}</p>
            <div className="fw-bold mb-2" style={{ fontSize: 20 }}>${quickView.price}</div>
            <button className="btn btn-primary w-100" style={{ borderRadius: 24, fontWeight: 600, background: '#FF9900', border: 'none', marginTop: 8 }} onClick={() => { addToCart(quickView); setQuickView(null); }}>Add to Cart</button>
          </div>
        </div>
      )}
    </>
  );
}

export default App;
