import React, { useState, useEffect } from 'react';
import './App.css';
import { FaShoppingCart, FaTshirt, FaFemale, FaMale, FaChild, FaHeadphones, FaCamera, FaGamepad, FaLaptop, FaTabletAlt, FaClock, FaVolumeUp, FaTimes, FaSearchPlus, FaShoePrints, FaKeyboard, FaMobileAlt, FaTv, FaCameraRetro, FaStar, FaTag, FaSuitcase, FaAppleAlt, FaUserCircle, FaSearch, FaCoffee, FaSun, FaLeaf, FaBlender, FaCar, FaHeart, FaFutbol, FaBasketballBall, FaStore, FaEllipsisH, FaCouch, FaPlane, FaPuzzlePiece, FaMotorcycle, FaBook, FaFacebookF, FaTwitter, FaYoutube, FaInstagram, FaHeart as FaHeartFilled, FaRegHeart } from 'react-icons/fa';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';

const categories = [
  { name: 'All', icon: <FaSearchPlus /> },
  { name: 'Electronics', icon: <FaClock /> },
  { name: 'Wearables', icon: <FaTshirt /> },
  { name: 'Audio', icon: <FaVolumeUp /> },
  { name: 'Photography', icon: <FaCamera /> },
  { name: 'Gaming', icon: <FaGamepad /> },
  { name: 'Computers', icon: <FaLaptop /> },
  { name: 'Tablets', icon: <FaTabletAlt /> },
  { name: 'Men', icon: <FaMale /> },
  { name: 'Women', icon: <FaFemale /> },
  { name: 'Kids', icon: <FaChild /> },
  { name: 'Books', icon: <FaBook /> },
  { name: 'Beauty', icon: <FaHeart /> },
  { name: 'Sports', icon: <FaFutbol /> },
  { name: 'Kitchen', icon: <FaCoffee /> },
  { name: 'Pets', icon: <FaAppleAlt /> },
  { name: 'Stationery', icon: <FaTag /> },
  { name: 'Baby', icon: <FaChild /> },
  { name: 'Health', icon: <FaLeaf /> },
  { name: 'Accessories', icon: <FaSun /> },
];

const homepageCategories = [
  { name: 'Grocery', icon: <FaAppleAlt />, img: 'https://img.icons8.com/color/96/000000/grocery-bag.png' },
  { name: 'Mobiles', icon: <FaMobileAlt />, img: 'https://img.icons8.com/color/96/000000/smartphone-tablet.png' },
  { name: 'Fashion', icon: <FaTshirt />, img: 'https://img.icons8.com/color/96/000000/t-shirt.png' },
  { name: 'Electronics', icon: <FaLaptop />, img: 'https://img.icons8.com/color/96/000000/laptop.png' },
  { name: 'Home & Furniture', icon: <FaCouch />, img: 'https://img.icons8.com/color/96/000000/sofa.png' },
  { name: 'Appliances', icon: <FaBlender />, img: 'https://img.icons8.com/color/96/000000/blender.png' },
  { name: 'Travel', icon: <FaPlane />, img: 'https://img.icons8.com/color/96/000000/airplane-take-off.png' },
  { name: 'Toys', icon: <FaPuzzlePiece />, img: 'https://img.icons8.com/color/96/000000/teddy-bear.png' },
  { name: 'Vehicles', icon: <FaMotorcycle />, img: 'https://img.icons8.com/color/96/000000/motorcycle.png' },
  { name: 'Books', icon: <FaBook />, img: 'https://img.icons8.com/color/96/000000/books.png' },
  { name: 'Beauty', icon: <FaHeart />, img: 'https://img.icons8.com/color/96/000000/lipstick.png' },
  { name: 'Sports', icon: <FaFutbol />, img: 'https://img.icons8.com/color/96/000000/soccer-ball.png' },
  { name: 'Kitchen', icon: <FaCoffee />, img: 'https://img.icons8.com/color/96/000000/kitchen-room.png' },
  { name: 'Pets', icon: <FaAppleAlt />, img: 'https://img.icons8.com/color/96/000000/dog.png' },
  { name: 'Stationery', icon: <FaTag />, img: 'https://img.icons8.com/color/96/000000/stationery.png' },
  { name: 'Baby', icon: <FaChild />, img: 'https://img.icons8.com/color/96/000000/baby.png' },
  { name: 'Health', icon: <FaLeaf />, img: 'https://img.icons8.com/color/96/000000/heart-health.png' },
  { name: 'Accessories', icon: <FaSun />, img: 'https://img.icons8.com/color/96/000000/sunglasses.png' },
];

function CartPage({ cart, updateQty, removeFromCart, removeAllCart, total }) {
  return (
    <div className="cart-page">
      <h2 style={{ fontWeight: 700, marginBottom: 24 }}>Your Cart</h2>
      {cart.length === 0 ? (
        <div style={{ textAlign: 'center', color: '#888', marginTop: 40 }}>Your cart is empty.</div>
      ) : (
        <>
          <div className="cart-items">
            {cart.map(item => (
              <div className="cart-item" key={item._id}>
                <div style={{ flex: 1 }}>
                  <div style={{ fontWeight: 600 }}>{item.name}</div>
                  <div style={{ color: '#888', fontSize: 13 }}>{item.category}</div>
                  <div style={{ fontSize: 14, marginTop: 4 }}>
                    <button onClick={() => updateQty(item._id, Math.max(1, item.qty - 1))} className="qty-btn">-</button>
                    <span style={{ margin: '0 8px' }}>{item.qty}</span>
                    <button onClick={() => updateQty(item._id, item.qty + 1)} className="qty-btn">+</button>
                  </div>
                </div>
                <div style={{ fontWeight: 600, marginLeft: 12 }}>${item.price * item.qty}</div>
                <FaTimes className="cart-remove" onClick={() => removeFromCart(item._id)} />
              </div>
            ))}
          </div>
          <div className="cart-footer">
            <div style={{ fontWeight: 700, fontSize: 18 }}>Total: ${total}</div>
            <button className="btn btn-success w-100 mt-2" style={{ borderRadius: 24, fontWeight: 600, background: '#28a745', border: 'none' }}
              onClick={() => { removeAllCart(); window.alert('Order placed successfully!'); }}>
              Checkout
            </button>
          </div>
        </>
      )}
    </div>
  );
}

function App() {
  const [cart, setCart] = useState(() => JSON.parse(localStorage.getItem('cart')) || []);
  const [category, setCategory] = useState('All');
  const [cartOpen, setCartOpen] = useState(false);
  const [toast, setToast] = useState('');
  const [quickView, setQuickView] = useState(null);
  const [search, setSearch] = useState('');
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [wishlist, setWishlist] = useState(() => JSON.parse(localStorage.getItem('wishlist')) || []);
  const navigate = useNavigate();

  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(cart));
  }, [cart]);

  useEffect(() => {
    localStorage.setItem('wishlist', JSON.stringify(wishlist));
  }, [wishlist]);

  useEffect(() => {
    if (toast) {
      const t = setTimeout(() => setToast(''), 2000);
      return () => clearTimeout(t);
    }
  }, [toast]);

  useEffect(() => {
    setLoading(true);
    fetch('http://localhost:5000/api/products')
      .then(res => res.json())
      .then(data => {
        setProducts(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching products:', error);
        setLoading(false);
      });
  }, []);

  const filteredProducts = products.filter(p =>
    (category === 'All' || p.category === category) &&
    (p.name.toLowerCase().includes(search.toLowerCase()) || (p.desc || '').toLowerCase().includes(search.toLowerCase()))
  );

  const addToCart = (product) => {
    setCart(prev => {
      const exists = prev.find(item => item._id === product._id);
      if (exists) {
        setToast('Increased quantity in cart!');
        return prev.map(item => item._id === product._id ? { ...item, qty: item.qty + 1 } : item);
      }
      setToast('Added to cart!');
      return [...prev, { ...product, qty: 1 }];
    });
  };

  const removeFromCart = (_id) => {
    setCart(prev => prev.filter(item => item._id !== _id));
    setToast('Removed from cart!');
  };

  const removeAllCart = () => {
    setCart([]);
    setToast('Cart cleared!');
  };

  const updateQty = (_id, qty) => {
    setCart(prev => prev.map(item => item._id === _id ? { ...item, qty } : item));
  };

  const total = cart.reduce((a, c) => a + c.price * c.qty, 0);

  const isWishlisted = (id) => wishlist.includes(id);
  const toggleWishlist = (id) => {
    setWishlist(prev => prev.includes(id) ? prev.filter(wid => wid !== id) : [...prev, id]);
  };

  // Product Quick View Modal
  const [modalProduct, setModalProduct] = useState(null);

  return (
    <>
      <nav className="custom-navbar">
        <div className="navbar-left">
          <span className="navbar-logo" style={{ cursor: 'pointer' }} onClick={() => navigate('/')}>ShopEase</span>
        </div>
        <div className="navbar-center">
          <div className="search-bar">
            <FaSearch className="search-icon" />
            <input
              type="text"
              className="form-control"
              placeholder="Search for products, brands and more"
              value={search}
              onChange={e => setSearch(e.target.value)}
            />
          </div>
        </div>
        <div className="navbar-right">
          <div className="navbar-action">
            <FaStore className="navbar-icon" />
            <span>Become a Seller</span>
          </div>
          <div className="navbar-action" onClick={() => navigate('/cart')} style={{ cursor: 'pointer' }}>
            <FaShoppingCart className="navbar-icon" />
            <span>Cart</span>
          </div>
          <div className="navbar-action login-dropdown">
            <FaUserCircle className="navbar-icon" />
            <span>Login</span>
          </div>
          <div className="navbar-action">
            <FaEllipsisH className="navbar-icon" />
          </div>
        </div>
      </nav>
      <Routes>
        <Route path="/" element={
          <>
            {/* Hero, Category Filter, Category Menu, Product Grid, Product Modal */}
            <div className="hero mt-4">
              <h1>Welcome to ShopEase</h1>
              <p>Your one-stop shop for the latest trends, gadgets, and essentials.</p>
            </div>
            <div className="category-filter mb-4">
              {categories.map(cat => (
                <button
                  key={cat.name}
                  className={`btn btn-category${category === cat.name ? ' active' : ''}`}
                  onClick={() => setCategory(cat.name)}
                >
                  {cat.icon} {cat.name}
                </button>
              ))}
            </div>
            <div className="category-menu">
              {homepageCategories.map(cat => (
                <div className="category-item" key={cat.name}>
                  <img src={cat.img} alt={cat.name} className="category-img" />
                  <div className="category-label">{cat.name}</div>
                </div>
              ))}
            </div>
            <div className="product-grid">
              {loading ? (
                <div style={{ gridColumn: '1/-1', textAlign: 'center', fontSize: 22, color: '#888', padding: 40 }}>Loading products...</div>
              ) : filteredProducts.length === 0 ? (
                <div style={{ gridColumn: '1/-1', textAlign: 'center', fontSize: 20, color: '#888', padding: 40 }}>No products found.</div>
              ) : filteredProducts.map(product => (
                <div className="product-card" key={product._id || product.id}>
                  <div style={{ position: 'relative', cursor: 'pointer' }} onClick={() => setModalProduct(product)}>
                    {product.image && (
                      <img
                        src={product.image}
                        alt={product.name}
                        style={{ width: '100%', height: 140, objectFit: 'cover', borderRadius: 12, marginBottom: 8 }}
                      />
                    )}
                    <div style={{ position: 'absolute', top: 8, right: 8, zIndex: 2 }} onClick={e => { e.stopPropagation(); toggleWishlist(product._id); }}>
                      {isWishlisted(product._id) ? <FaHeartFilled color="#e74c3c" size={22} /> : <FaRegHeart color="#e74c3c" size={22} />}
                    </div>
                  </div>
                  <h5 style={{ fontWeight: 700, fontSize: 18, margin: '8px 0 4px 0', whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis', width: '100%' }}>{product.name}</h5>
                  <div style={{ color: '#888', fontSize: 13, marginBottom: 4 }}>{product.category}</div>
                  <p className="text-muted" style={{ fontSize: 14, minHeight: 36, margin: '0 0 8px 0', color: '#666' }}>{product.description || ''}</p>
                  <p className="fw-bold" style={{ fontSize: 18, margin: 0 }}>${product.price}</p>
                  <button className="btn btn-primary w-100 mb-2" style={{ borderRadius: 24, fontWeight: 600, background: '#FF9900', border: 'none', marginTop: 8 }} onClick={() => addToCart(product)}>
                    Add to Cart
                  </button>
                </div>
              ))}
            </div>
            {/* Product Details Modal */}
            {modalProduct && (
              <div className="modal-overlay" onClick={() => setModalProduct(null)}>
                <div className="modal-content" onClick={e => e.stopPropagation()}>
                  <button className="modal-close" onClick={() => setModalProduct(null)}><FaTimes /></button>
                  {modalProduct.image && (
                    <img src={modalProduct.image} alt={modalProduct.name} style={{ width: '100%', maxHeight: 260, objectFit: 'contain', borderRadius: 16, marginBottom: 16 }} />
                  )}
                  <h2 style={{ fontWeight: 700 }}>{modalProduct.name}</h2>
                  <div style={{ color: '#888', fontSize: 15, marginBottom: 8 }}>{modalProduct.category}</div>
                  <p style={{ fontSize: 16, color: '#444', marginBottom: 16 }}>{modalProduct.description}</p>
                  <div style={{ fontWeight: 700, fontSize: 22, marginBottom: 16 }}>${modalProduct.price}</div>
                  <button className="btn btn-primary w-100 mb-2" style={{ borderRadius: 24, fontWeight: 600, background: '#FF9900', border: 'none' }} onClick={() => { addToCart(modalProduct); setToast('Added to cart!'); }}>
                    Add to Cart
                  </button>
                  <button className="btn btn-outline-danger w-100" style={{ borderRadius: 24, fontWeight: 600, border: '2px solid #e74c3c', color: '#e74c3c' }} onClick={() => { toggleWishlist(modalProduct._id); setToast(isWishlisted(modalProduct._id) ? 'Removed from wishlist!' : 'Added to wishlist!'); }}>
                    {isWishlisted(modalProduct._id) ? 'Remove from Wishlist' : 'Add to Wishlist'}
                  </button>
                </div>
              </div>
            )}
          </>
        } />
        <Route path="/cart" element={
          <CartPage cart={cart} updateQty={updateQty} removeFromCart={removeFromCart} removeAllCart={removeAllCart} total={total} />
        } />
      </Routes>
      {/* Toast Notification */}
      {toast && <div className="toast-message">{toast}</div>}

      {/* Quick View Modal */}
      {quickView && (
        <div style={{
          position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh',
          background: 'rgba(0,0,0,0.3)', zIndex: 3000, display: 'flex', alignItems: 'center', justifyContent: 'center'
        }} onClick={() => setQuickView(null)}>
          <div style={{ background: '#fff', borderRadius: 16, padding: 32, minWidth: 320, minHeight: 220, position: 'relative', boxShadow: '0 8px 32px rgba(0,0,0,0.16)', maxWidth: 400 }} onClick={e => e.stopPropagation()}>
            <button style={{ position: 'absolute', top: 12, right: 12, background: 'none', border: 'none', fontSize: 24, color: '#FF9900', cursor: 'pointer' }} onClick={() => setQuickView(null)}><FaTimes /></button>
            {quickView.badge && <span className="badge" style={{ position: 'absolute', top: 16, left: 16, background: '#FF9900', color: '#fff', borderRadius: 12, padding: '2px 12px', fontWeight: 600, fontSize: 13 }}>{quickView.badge}</span>}
            <img src={quickView.img} alt={quickView.name} style={{ width: 140, height: 140, objectFit: 'contain', marginBottom: 16, borderRadius: 12 }} />
            <h4 style={{ display: 'flex', alignItems: 'center', gap: 8, fontWeight: 700, fontSize: 22, margin: '8px 0 4px 0', whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis', width: '100%' }}>{React.isValidElement(quickView.icon) ? quickView.icon : null} {quickView.name}</h4>
            <div style={{ display: 'flex', alignItems: 'center', gap: 4, marginBottom: 4 }}>
              <>{getStars(quickView.rating)}</>
              <span style={{ color: '#888', fontSize: 13, marginLeft: 4 }}>{quickView.rating}</span>
            </div>
            <p className="text-muted" style={{ fontSize: 15, minHeight: 36, margin: '0 0 8px 0', color: '#666' }}>{quickView.desc}</p>
            <div className="fw-bold mb-2" style={{ fontSize: 20 }}>${quickView.price}</div>
            <button className="btn btn-primary w-100" style={{ borderRadius: 24, fontWeight: 600, background: '#FF9900', border: 'none', marginTop: 8 }} onClick={() => { addToCart(quickView); setQuickView(null); }}>Add to Cart</button>
          </div>
        </div>
      )}
      {/* Footer */}
      <footer className="footer-section" style={{ background: '#222', color: '#ccc', marginTop: 48, padding: '40px 0 0 0', fontSize: 15 }}>
        <div className="container" style={{ maxWidth: 1400, margin: '0 auto', padding: '0 24px' }}>
          <div className="row" style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-between', borderBottom: '1px solid #444', paddingBottom: 32 }}>
            <div className="footer-col" style={{ minWidth: 180, marginBottom: 24 }}>
              <h5 style={{ color: '#fff', fontWeight: 700, marginBottom: 16 }}>ABOUT</h5>
              <ul style={{ listStyle: 'none', padding: 0, margin: 0 }}>
                <li><a href="#" style={{ color: '#fff', textDecoration: 'none' }}>Contact Us</a></li>
                <li><a href="#" style={{ color: '#fff', textDecoration: 'none' }}>About Us</a></li>
                <li><a href="#" style={{ color: '#fff', textDecoration: 'none' }}>Careers</a></li>
                <li><a href="#" style={{ color: '#fff', textDecoration: 'none' }}>Stories</a></li>
                <li><a href="#" style={{ color: '#fff', textDecoration: 'none' }}>Press</a></li>
                <li><a href="#" style={{ color: '#fff', textDecoration: 'none' }}>Corporate Information</a></li>
              </ul>
            </div>
            <div className="footer-col" style={{ minWidth: 180, marginBottom: 24 }}>
              <h5 style={{ color: '#fff', fontWeight: 700, marginBottom: 16 }}>HELP</h5>
              <ul style={{ listStyle: 'none', padding: 0, margin: 0 }}>
                <li><a href="#" style={{ color: '#fff', textDecoration: 'none' }}>Payments</a></li>
                <li><a href="#" style={{ color: '#fff', textDecoration: 'none' }}>Shipping</a></li>
                <li><a href="#" style={{ color: '#fff', textDecoration: 'none' }}>Cancellation & Returns</a></li>
                <li><a href="#" style={{ color: '#fff', textDecoration: 'none' }}>FAQ</a></li>
              </ul>
            </div>
            <div className="footer-col" style={{ minWidth: 180, marginBottom: 24 }}>
              <h5 style={{ color: '#fff', fontWeight: 700, marginBottom: 16 }}>CONSUMER POLICY</h5>
              <ul style={{ listStyle: 'none', padding: 0, margin: 0 }}>
                <li><a href="#" style={{ color: '#fff', textDecoration: 'none' }}>Cancellation & Returns</a></li>
                <li><a href="#" style={{ color: '#fff', textDecoration: 'none' }}>Terms Of Use</a></li>
                <li><a href="#" style={{ color: '#fff', textDecoration: 'none' }}>Security</a></li>
                <li><a href="#" style={{ color: '#fff', textDecoration: 'none' }}>Privacy</a></li>
                <li><a href="#" style={{ color: '#fff', textDecoration: 'none' }}>Sitemap</a></li>
                <li><a href="#" style={{ color: '#fff', textDecoration: 'none' }}>Grievance Redressal</a></li>
                <li><a href="#" style={{ color: '#fff', textDecoration: 'none' }}>EPR Compliance</a></li>
              </ul>
            </div>
            <div className="footer-col" style={{ minWidth: 180, marginBottom: 24 }}>
              <h5 style={{ color: '#fff', fontWeight: 700, marginBottom: 16 }}>GROUP COMPANIES</h5>
              <ul style={{ listStyle: 'none', padding: 0, margin: 0 }}>
                <li><a href="#" style={{ color: '#fff', textDecoration: 'none' }}>Myntra</a></li>
                <li><a href="#" style={{ color: '#fff', textDecoration: 'none' }}>Cleartrip</a></li>
                <li><a href="#" style={{ color: '#fff', textDecoration: 'none' }}>Shopsy</a></li>
              </ul>
            </div>
            <div className="footer-col" style={{ minWidth: 260, marginBottom: 24 }}>
              <h5 style={{ color: '#fff', fontWeight: 700, marginBottom: 16 }}>Mail Us:</h5>
              <div style={{ color: '#ccc', fontSize: 14 }}>
                ShopEase Internet Pvt Ltd,<br />
                123 E-Commerce Ave,<br />
                Tech City, India<br />
                <br />
                <span style={{ color: '#fff' }}>Social:</span>
                <span style={{ marginLeft: 8, display: 'flex', gap: 8 }}>
                  <a href="#" style={{ color: '#fff' }}><FaFacebookF /></a>
                  <a href="#" style={{ color: '#fff' }}><FaTwitter /></a>
                  <a href="#" style={{ color: '#fff' }}><FaYoutube /></a>
                  <a href="#" style={{ color: '#fff' }}><FaInstagram /></a>
                </span>
              </div>
            </div>
          </div>
          <div className="footer-bottom" style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-between', alignItems: 'center', padding: '24px 0', color: '#aaa', fontSize: 14 }}>
            <div style={{ display: 'flex', gap: 24, flexWrap: 'wrap', alignItems: 'center' }}>
              <span><i className="fas fa-store"></i> Become a Seller</span>
              <span><i className="fas fa-bullhorn"></i> Advertise</span>
              <span><i className="fas fa-gift"></i> Gift Cards</span>
              <span><i className="fas fa-question-circle"></i> Help Center</span>
            </div>
            <div style={{ textAlign: 'right' }}>
               2024 ShopEase.com &nbsp;
              <img src="https://img.icons8.com/color/32/000000/visa.png" alt="Visa" style={{ verticalAlign: 'middle', marginLeft: 4 }} />
              <img src="https://img.icons8.com/color/32/000000/mastercard-logo.png" alt="Mastercard" style={{ verticalAlign: 'middle', marginLeft: 4 }} />
              <img src="https://img.icons8.com/color/32/000000/amex.png" alt="Amex" style={{ verticalAlign: 'middle', marginLeft: 4 }} />
              <img src="https://img.icons8.com/color/32/000000/rupay.png" alt="RuPay" style={{ verticalAlign: 'middle', marginLeft: 4 }} />
            </div>
          </div>
        </div>
      </footer>
    </>
  );
}

export default function WrappedApp() {
  return (
    <Router>
      <App />
    </Router>
  );
}
