import React, { useState, useEffect } from 'react';
import './App.css';
import { FaShoppingCart, FaTshirt, FaFemale, FaMale, FaChild, FaHeadphones, FaCamera, FaGamepad, FaLaptop, FaTabletAlt, FaClock, FaVolumeUp, FaTimes, FaSearchPlus, FaShoePrints, FaKeyboard, FaMobileAlt, FaTv, FaCameraRetro, FaStar, FaTag, FaSuitcase, FaAppleAlt } from 'react-icons/fa';

const categories = [
  { name: 'All', icon: <FaSearchPlus /> },
  { name: 'Electronics', icon: <FaClock /> },
  { name: 'Wearables', icon: <FaTshirt /> },
  { name: 'Audio', icon: <FaVolumeUp /> },
  { name: 'Photography', icon: <FaCamera /> },
  { name: 'Gaming', icon: <FaGamepad /> },
  { name: 'Computers', icon: <FaLaptop /> },
  { name: 'Tablets', icon: <FaTabletAlt /> },
  { name: 'Men', icon: <FaMale /> },
  { name: 'Women', icon: <FaFemale /> },
  { name: 'Kids', icon: <FaChild /> },
];

const sampleProducts = [
  { id: 1, name: 'Smart Watch', price: 99, category: 'Wearables', img: 'https://images.unsplash.com/photo-1511707171634-5f897ff02aa9?auto=format&fit=crop&w=400&q=80', icon: <FaClock /> },
  { id: 2, name: 'Wireless Headphones', price: 59, category: 'Audio', img: 'https://images.unsplash.com/photo-1511367461989-f85a21fda167?auto=format&fit=crop&w=400&q=80', icon: <FaHeadphones /> },
  { id: 3, name: 'Gaming Mouse', price: 39, category: 'Gaming', img: 'https://images.unsplash.com/photo-1517336714731-489689fd1ca8?auto=format&fit=crop&w=400&q=80', icon: <FaGamepad /> },
  { id: 4, name: 'DSLR Camera', price: 499, category: 'Photography', img: 'https://images.unsplash.com/photo-1519125323398-675f0ddb6308?auto=format&fit=crop&w=400&q=80', icon: <FaCameraRetro /> },
  { id: 5, name: 'Laptop', price: 899, category: 'Computers', img: 'https://images.unsplash.com/photo-1517336714731-489689fd1ca8?auto=format&fit=crop&w=400&q=80', icon: <FaLaptop /> },
  { id: 6, name: 'Tablet', price: 299, category: 'Tablets', img: 'https://images.unsplash.com/photo-1515378791036-0648a3ef77b2?auto=format&fit=crop&w=400&q=80', icon: <FaTabletAlt /> },
  { id: 7, name: "Men's T-Shirt", price: 19, category: 'Men', img: 'https://images.unsplash.com/photo-1512436991641-6745cdb1723f?auto=format&fit=crop&w=400&q=80', icon: <FaTshirt /> },
  { id: 8, name: "Women's Dress", price: 29, category: 'Women', img: 'https://images.unsplash.com/photo-1517841905240-472988babdf9?auto=format&fit=crop&w=400&q=80', icon: <FaFemale /> },
  { id: 9, name: "Kids' Jacket", price: 25, category: 'Kids', img: 'https://images.unsplash.com/photo-1503342217505-b0a15ec3261c?auto=format&fit=crop&w=400&q=80', icon: <FaChild /> },
  { id: 10, name: 'Bluetooth Speaker', price: 45, category: 'Audio', img: 'https://images.unsplash.com/photo-1465101046530-73398c7f28ca?auto=format&fit=crop&w=400&q=80', icon: <FaVolumeUp /> },
  { id: 11, name: 'Fitness Tracker', price: 79, category: 'Wearables', img: 'https://images.unsplash.com/photo-1519864600265-abb23847ef2c?auto=format&fit=crop&w=400&q=80', icon: <FaClock /> },
  { id: 12, name: 'Camera Lens', price: 199, category: 'Photography', img: 'https://images.unsplash.com/photo-1464983953574-0892a716854b?auto=format&fit=crop&w=400&q=80', icon: <FaCamera /> },
  { id: 13, name: 'Gaming Keyboard', price: 69, category: 'Gaming', img: 'https://images.unsplash.com/photo-1517336714731-489689fd1ca8?auto=format&fit=crop&w=400&q=80', icon: <FaKeyboard /> },
  { id: 14, name: 'Backpack', price: 49, category: 'Men', img: 'https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=400&q=80', icon: <FaSuitcase /> },
  { id: 15, name: 'Running Shoes', price: 89, category: 'Women', img: 'https://images.unsplash.com/photo-1513104890138-7c749659a591?auto=format&fit=crop&w=400&q=80', icon: <FaShoePrints /> },
  { id: 16, name: 'Smartphone', price: 499, category: 'Electronics', img: 'https://images.unsplash.com/photo-1510557880182-3d4d3c1b2604?auto=format&fit=crop&w=400&q=80', icon: <FaMobileAlt /> },
  { id: 17, name: '4K TV', price: 799, category: 'Electronics', img: 'https://images.unsplash.com/photo-1519125323398-675f0ddb6308?auto=format&fit=crop&w=400&q=80', icon: <FaTv /> },
  { id: 18, name: 'Apple', price: 2, category: 'Kids', img: 'https://images.unsplash.com/photo-1502741338009-cac2772e18bc?auto=format&fit=crop&w=400&q=80', icon: <FaAppleAlt /> },
  { id: 19, name: 'Sale Tag', price: 5, category: 'Kids', img: 'https://images.unsplash.com/photo-1503342217505-b0a15ec3261c?auto=format&fit=crop&w=400&q=80', icon: <FaTag /> },
  { id: 20, name: 'Star Toy', price: 15, category: 'Kids', img: 'https://images.unsplash.com/photo-1503342217505-b0a15ec3261c?auto=format&fit=crop&w=400&q=80', icon: <FaStar /> },
];

function App() {
  const [cart, setCart] = useState(() => JSON.parse(localStorage.getItem('cart')) || []);
  const [category, setCategory] = useState('All');
  const [cartOpen, setCartOpen] = useState(false);
  const [toast, setToast] = useState('');
  const [quickView, setQuickView] = useState(null);

  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(cart));
  }, [cart]);

  useEffect(() => {
    if (toast) {
      const t = setTimeout(() => setToast(''), 2000);
      return () => clearTimeout(t);
    }
  }, [toast]);

  const filteredProducts = category === 'All'
    ? sampleProducts
    : sampleProducts.filter(p => p.category === category);

  const addToCart = (product) => {
    setCart(prev => {
      const exists = prev.find(item => item.id === product.id);
      if (exists) {
        setToast('Increased quantity in cart!');
        return prev.map(item => item.id === product.id ? { ...item, qty: item.qty + 1 } : item);
      }
      setToast('Added to cart!');
      return [...prev, { ...product, qty: 1 }];
    });
  };

  const removeFromCart = (id) => {
    setCart(prev => prev.filter(item => item.id !== id));
    setToast('Removed from cart!');
  };

  const updateQty = (id, qty) => {
    setCart(prev => prev.map(item => item.id === id ? { ...item, qty } : item));
  };

  const total = cart.reduce((a, c) => a + c.price * c.qty, 0);

  return (
    <>
      {/* Navbar */}
      <nav className="navbar">
        <span className="navbar-brand">E-Shop</span>
        <button className="btn btn-category" onClick={() => setCartOpen(true)}>
          <FaShoppingCart /> Cart ({cart.reduce((a, c) => a + c.qty, 0)})
        </button>
      </nav>

      {/* Hero */}
      <div className="hero mt-4">
        <h1>Modern E-Commerce Store</h1>
        <p>Beautiful, interactive shopping experience. Browse by category and add to cart!</p>
      </div>

      {/* Category Filter */}
      <div className="category-filter mb-4">
        {categories.map(cat => (
          <button
            key={cat.name}
            className={`btn btn-category${category === cat.name ? ' active' : ''}`}
            onClick={() => setCategory(cat.name)}
          >
            {cat.icon} {cat.name}
          </button>
        ))}
      </div>

      {/* Product Grid */}
      <div className="product-grid">
        {filteredProducts.map(product => (
          <div className="product-card" key={product.id}>
            <img src={product.img} alt={product.name} />
            <h5 style={{display:'flex',alignItems:'center',gap:8}}>{product.icon} {product.name}</h5>
            <p className="text-muted">{product.category}</p>
            <p className="fw-bold">${product.price}</p>
            <button className="btn btn-primary w-100 mb-2" onClick={() => addToCart(product)}>
              Add to Cart
            </button>
            <button className="quick-view" title="Quick View" onClick={() => setQuickView(product)}>
              <FaSearchPlus />
            </button>
          </div>
        ))}
      </div>

      {/* Cart Drawer */}
      <div className={`cart-drawer${cartOpen ? ' open' : ''}`}>
        <div className="cart-drawer-header">
          <h5><FaShoppingCart /> Cart</h5>
          <button className="cart-drawer-close" onClick={() => setCartOpen(false)}><FaTimes /></button>
        </div>
        <div className="cart-drawer-list">
          {cart.length === 0 ? <p className="text-muted">Cart is empty</p> : (
            <ul className="list-unstyled">
              {cart.map(item => (
                <li key={item.id} className="d-flex align-items-center mb-2">
                  <img src={item.img} alt={item.name} style={{ width: 36, height: 36, marginRight: 8 }} />
                  <span className="me-2">{item.name}</span>
                  <input type="number" min="1" value={item.qty} onChange={e => updateQty(item.id, Number(e.target.value))} style={{ width: 50 }} className="form-control form-control-sm mx-2" />
                  <span className="fw-bold">${item.price * item.qty}</span>
                  <button className="btn btn-danger btn-sm ms-2" onClick={() => removeFromCart(item.id)}>&times;</button>
                </li>
              ))}
            </ul>
          )}
        </div>
        <div className="cart-drawer-footer">
          <div className="fw-bold">Total: ${total}</div>
          <button className="btn btn-success mt-2 w-100" disabled={cart.length === 0}>Checkout</button>
        </div>
      </div>

      {/* Toast Notification */}
      <div className={`toast${toast ? ' show' : ''}`}>{toast}</div>

      {/* Quick View Modal */}
      {quickView && (
        <div style={{
          position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh',
          background: 'rgba(0,0,0,0.3)', zIndex: 3000, display: 'flex', alignItems: 'center', justifyContent: 'center'
        }} onClick={() => setQuickView(null)}>
          <div style={{ background: '#fff', borderRadius: 16, padding: 32, minWidth: 300, minHeight: 200, position: 'relative', boxShadow: '0 8px 32px rgba(0,0,0,0.16)' }} onClick={e => e.stopPropagation()}>
            <button style={{ position: 'absolute', top: 12, right: 12, background: 'none', border: 'none', fontSize: 24, color: '#FF9900', cursor: 'pointer' }} onClick={() => setQuickView(null)}><FaTimes /></button>
            <img src={quickView.img} alt={quickView.name} style={{ width: 120, height: 120, objectFit: 'contain', marginBottom: 16 }} />
            <h4 style={{display:'flex',alignItems:'center',gap:8}}>{quickView.icon} {quickView.name}</h4>
            <p className="text-muted">{quickView.category}</p>
            <div className="fw-bold mb-2">${quickView.price}</div>
            <button className="btn btn-primary w-100" onClick={() => { addToCart(quickView); setQuickView(null); }}>Add to Cart</button>
          </div>
        </div>
      )}
    </>
  );
}

export default App;
