              <img src="https://img.icons8.com/color/32/000000/visa.png" alt="Visa" style={{ verticalAlign: 'middle', marginLeft: 4 }} />
              <img src="https://img.icons8.com/color/32/000000/mastercard-logo.png" alt="Mastercard" style={{ verticalAlign: 'middle', marginLeft: 4 }} />
              <img src="https://img.icons8.com/color/32/000000/amex.png" alt="Amex" style={{ verticalAlign: 'middle', marginLeft: 4 }} />
              <img src="https://img.icons8.com/color/32/000000/rupay.png" alt="RuPay" style={{ verticalAlign: 'middle', marginLeft: 4 }} />
